# ... (code for shell_worker function)
def shell_worker(conn):
    shell = TerminalInteractiveShell()
    try:
        while True:
            try:
                msg = conn.recv()
            except EOFError:
                break
            if not isinstance(msg, dict):
                continue
            cmd = msg.get("cmd")
            if cmd == "exec":
                code = msg.get("code", "")
                exec_id = msg.get("id")
                traceback_verbosity = msg.get("traceback_verbosity", "Plain")
                shell.InteractiveTB.set_mode(mode=traceback_verbosity)
                stdout_buf = io.StringIO()
                stderr_buf = io.StringIO()
                try:
                    with redirect_stdout(stdout_buf), redirect_stderr(stderr_buf):
                        res = shell.run_cell(code)
                    conn.send(
                        {
                            "status": "ok",
                            "id": exec_id,
                            "result_repr": repr(getattr(res, "result", None)),
                            "stdout": stdout_buf.getvalue(),
                            "stderr": stderr_buf.getvalue(),
                            "has_error": res.error_before_exec or res.error_in_exec,
                        }
                    )
                except KeyboardInterrupt:
                    conn.send(
                        {
                            "status": "interrupted",
                            "id": exec_id,
                            "stdout": stdout_buf.getvalue(),
                            "stderr": stderr_buf.getvalue(),
                        }
                    )
                except Exception:
                    conn.send(
                        {
                            "status": "error",
                            "id": exec_id,
                            "traceback": traceback.format_exc(),
                            "stdout": stdout_buf.getvalue(),
                            "stderr": stderr_buf.getvalue(),
                        }
                    )
            elif cmd == "shutdown":
                break
    finally:
        try:
            conn.close()
        except Exception:
            pass

